let burgerMenu=document.querySelector(".block-menu__burger"),popUpMenus=document.querySelector(".menu-burger-list__pop-up-menus"),activeBurgerMenu=e=>{e.preventDefault(),e.stopPropagation(),e.target.classList.contains("burger")?(burgerMenu.classList.remove("burger"),popUpMenus.classList.remove("menus")):(burgerMenu.classList.add("burger"),popUpMenus.classList.add("menus"))};burgerMenu.addEventListener("click",activeBurgerMenu);